import "core-js/modules/es.array.push.js";
import * as THREE from 'three';
export default {
  name: 'ThreeDRoadmap',
  mounted() {
    this.initThreeJS();
  },
  methods: {
    initThreeJS() {
      // Basic Scene Setup
      const scene = new THREE.Scene();
      const camera = new THREE.PerspectiveCamera(75, this.$refs.threeContainer.offsetWidth / this.$refs.threeContainer.offsetHeight, 0.1, 1000);
      const renderer = new THREE.WebGLRenderer({
        antialias: true
      });
      renderer.setSize(this.$refs.threeContainer.offsetWidth, this.$refs.threeContainer.offsetHeight);
      this.$refs.threeContainer.appendChild(renderer.domElement);

      // Ambient Light
      const ambientLight = new THREE.AmbientLight(0xffffff, 0.5); // Soft white light
      scene.add(ambientLight);

      // Directional Light
      const light = new THREE.PointLight(0xffffff, 1, 100);
      light.position.set(10, 10, 10);
      scene.add(light);

      // Create Road
      const roadShape = new THREE.Shape();
      roadShape.moveTo(-5, -1);
      roadShape.lineTo(5, -1);
      roadShape.lineTo(5, 1);
      roadShape.lineTo(-5, 1);
      roadShape.lineTo(-5, -1);
      const extrudeSettings = {
        steps: 2,
        depth: 0.2,
        bevelEnabled: false
      };
      const roadGeometry = new THREE.ExtrudeGeometry(roadShape, extrudeSettings);
      const roadMaterial = new THREE.MeshLambertMaterial({
        color: 0x333333
      });
      const roadMesh = new THREE.Mesh(roadGeometry, roadMaterial);
      roadMesh.rotation.x = Math.PI / 2;
      roadMesh.position.y = 0;
      scene.add(roadMesh);

      // Create Milestones
      const milestoneGeometry = new THREE.CylinderGeometry(0.3, 0.3, 0.5, 32);
      const milestoneMaterial = new THREE.MeshLambertMaterial({
        color: 0xff0000
      });
      const milestones = [];
      for (let i = 0; i < 4; i++) {
        const milestoneMesh = new THREE.Mesh(milestoneGeometry, milestoneMaterial);
        milestoneMesh.position.set(-4 + i * 3, 0.5, 0);
        scene.add(milestoneMesh);
        milestones.push(milestoneMesh);
      }

      // Add Text Labels
      const loader = new THREE.FontLoader();
      loader.load('https://threejs.org/examples/fonts/helvetiker_regular.typeface.json', font => {
        for (let i = 0; i < milestones.length; i++) {
          const textGeometry = new THREE.TextGeometry(`Step ${i + 1}`, {
            font: font,
            size: 0.5,
            height: 0.1
          });
          const textMaterial = new THREE.MeshBasicMaterial({
            color: 0x0000ff
          });
          const textMesh = new THREE.Mesh(textGeometry, textMaterial);
          textMesh.position.set(-4 + i * 3 - 0.7, 1.5, 0);
          scene.add(textMesh);
        }
      });

      // Camera Position
      camera.position.z = 10;
      camera.position.y = 2;
      camera.lookAt(0, 0, 0);

      // Animation Loop
      const animate = () => {
        requestAnimationFrame(animate);
        renderer.render(scene, camera);
      };
      animate();

      // Handle resize
      window.addEventListener('resize', () => {
        camera.aspect = this.$refs.threeContainer.offsetWidth / this.$refs.threeContainer.offsetHeight;
        camera.updateProjectionMatrix();
        renderer.setSize(this.$refs.threeContainer.offsetWidth, this.$refs.threeContainer.offsetHeight);
      });
    }
  }
};
